<template>    <router-link class="item" :to="{name:'integralProduct',query:{id:item.id}}" tag="li">        <div class="logo">            <img :src="item.thumb"/>        </div>        <div class="title">            {{item.title}}        </div>        <div class="tip">            <div class="credit">                <i>{{item.credit}}</i>积分                <span v-if="item.money>0">+￥{{item.money}}</span>            </div>            <div class="type" v-if="item.type==0">兑换</div>            <div class="type" v-if="item.type==1">抽奖</div>        </div>    </router-link></template><script>    import defaultAvatar from '@/assets/images/test.png'    export default {        data () {            return {                test: defaultAvatar            }        },        props: {            item: {                 type: Object,                 required: true            },            jumpTo: {                type: String,                required: true            },            jumpQuery: {                type: Object            }        },        methods: {            tap (id) {                this.$router.push({name: 'product',query:{id:id}})            }        }    }</script><style lang="less" scoped>    @import '../../../assets/less/index.less';    .item {        width: 50%;        padding: .1rem;        box-sizing: border-box;        .logo {            width: 100%;            img {                width: 1.6rem;                height: 1.6rem;                margin: .05rem;                display: block;            }        }        .title {            .text-overflow(2);            color:#666;            font-size:.12rem;            text-align: left;        }        .tip {            display:flex;            justify-content:space-between;            align-items:center;            padding:.1rem 0;            text-align: left;            .credit {                flex: 1;                font-size: .1rem;                i {                    font-size:.14rem;                    color:#ff5654;                    font-weight:600;                    margin-right:.05rem;                }                span {                    font-size:14px;                    color:#ff5654;                    font-weight:600;                    /*margin-left:.05rem;*/                }            }            .type {                font-size:.12rem;                padding:.01rem .03rem;                background-color:#ff5654;                border-radius:.03rem;                color:#fff;                margin:0 .05rem;            }        }    }</style>