<!--确认订单和订单详情的商品信息 --><template>    <li>        <div class="logo">            <img :src="item.thumb">        </div>        <div class="info">            <p>{{item.title}}</p>            <div class="goods-option">                规格： {{item.optiontitle}}            </div>        </div>        <div class="num">            <div class="goods-price">                ¥                <span class="goods-intPrice">{{item.marketprice | calculatePrice1}}.</span>                <span class="goods-folatPrice">{{item.marketprice | calculatePrice2}}</span>            </div>            <div class="goods-num">X<i>{{item.total}}</i></div>        </div>        <!--<div class="logo">            <img src="">        </div>        <div class="info">            <p>苹果 Apple iPhone 8 plus 热销中</p>            <div class="goods-option">                规格： 64G公开版+银色            </div>        </div>        <div class="num">            <div class="goods-price">                ¥                <span class="goods-intPrice">6618.</span>                <span class="goods-folatPrice">00</span>            </div>            <div class="goods-num">X<i>1</i></div>        </div>-->    </li></template><script>    export default {        props: {            item: {                type: Object,                required: true            },        },        filters: {            calculatePrice1(value) {                let num = '';                if (typeof value == 'number') {                    num = Math.floor(value) || 0                } else if (typeof value == 'string') {                    num = Math.floor(Number(value)) || 0                } else if (typeof value == 'undefined' ) {                    return                }                return num || 0            },            calculatePrice2(value) {                let num = ''                if (typeof value == 'number') {                    num = value.toFixed(2).toString().split('.')[1]                }                if (typeof value == 'string') {                    num = value.split('.')[1] || '00'                }                if (typeof value == 'undefined') {                    return                }                return num.length == 0 ? num + '00' : num.length == 1 ? num + '0' : num || '00'            },        },        components: {        }    }</script><style lang="less" scoped="">    @import '../../../assets/less/index.less';    li {        height: 0.7rem;        padding: 0.1rem;        background: #fafafa;        display: flex;        .logo {            width: .5rem;            height: .5rem;            img {                width: 100%;                height: 100%;            }        }        .info {            flex: 1;            height: 100%;            margin-left: .1rem;            p {                font-size: 0.12rem;                color: #666;                text-align: left;                height: .32rem;                .text-overflow(2)            }            .goods-option {                font-size: 0.1rem;                color: #999;                text-align: left;                height: .16rem;            }        }        .num {            width: .8rem;            height: 100%;            .goods-price {                color: red;                font-size: .12rem;                .goods-intPrice {                    font-size: .18rem;                }                .goods-folatPrice {                    font-size: .12rem;                }            }            .goods-num {                font-size: 0.12rem;                text-align: right;                color: red;            }        }    }</style>