    complete: function () {                    }                })            },        },        components: {        },        mounted() {            this.getDetail()        },        created() {        }    }</script><style lang="less" scoped>    @import '../../../assets/less/common.less';    .main {        .page-view(100);        .container {            .scroll-view(100%);            .header-height;            .head-back{                position:relative;                height:2.4rem;                background:#fe5d5c;                overflow:hidden;                .top-layer{                    width:0;                    height:1.6rem;                    border-left:4rem solid #f74a4a;                    border-right:4rem solid #f74a4a;                    border-bottom:.5rem solid transparent;                    position:absolute;                    left:50%;                    margin-left:-4rem;                    top:0;                    z-index:1;                }                .line-layer{                    width:0;                    height:1rem;                    border-left:4rem solid #ffcf6f;                    border-right:4rem solid #ffcf6f;                    border-top:.5rem solid transparent;                    position:absolute;                    left:50%;                    margin-left:-4rem;                    bottom:0;                    z-index:3;                }                .bottom-layer{                    width:0;                    height:.95rem;                    border-left:4rem solid #fe5d5c;                    border-right:4rem solid #fe5d5c;                    border-top:.5rem solid transparent;                    position:absolute;                    left:50%;                    margin-left:-4rem ;                    bottom:-.01rem;                    z-index:4;                }                .gold{                    height:.5rem;                    widt