<template>    <div class="main">        <mt-header title="火爆拼团" fixed>            <router-link to="/" slot="left">                <mt-button icon="back"></mt-button>            </router-link>        </mt-header>        <div class="container">            <search ref="search" @submit="search"></search>            <tip :tip="tips">            <ul class="goods" v-infinite-scroll="loadList" infinite-scroll-disabled="loading" infinite-scroll-distance="10">                <group-list v-for="(i,index) in list" :item="i" jumpTo="activityProduct" :key="index"></group-list>            </ul>            </tip>        </div>        <group-bar></group-bar>        <router-view></router-view>    </div></template><script>    import util from '../../../api/util'    import list from './../../../assets/mixins/list'    import groupBar from '../../common/Tabbar/GroupBar.vue'    import search from '../../common/base/search.vue'    import groupList from '../../common/list/groupList.vue'    import Tip from '../../common/base/Tip.vue'    import InfiniteScroll from 'mint-ui'    import style from './../../../assets/mixins/style'    export default {        data() {            return {                id:'',                tips:{                }            }        },        mixins:[list,style],        prop:{        },        methods: {            init(){                console.log(111)                let _this=this                _this.id = _this.$route.query.id;//                _this.keyword= _this.$route.query.keywords                let params={                    url:'groups.goods.lists',                    opt:{                        cateid:  _this.id,                        keywords:_this.$refs.search.keywords,                    }                }                this.listInit().then(this.getList(params))            },            loadList(){                let _this=this                let params={                    url:'groups.goods.lists',                    opt:{                        cateid: _this.id,                        keywords:_this.$refs.search.keywords,                    }                }                this.loadMore(params)            },            search(key){                let params={                    url:'groups.goods.lists',                    opt:{                        cateid: '',                        keywords:key,                    }                }                this.id=''//                this.keywords=key                this.listInit().then(this.getList(params))            }        },        components:{            groupBar,            search,            groupList,            Tip        },        mounted(){            this.init()        }    }</script><style lang="less" scoped>    @import '../../../assets/less/index.less';    .main {        .page-view(100);        .container {            height: 100%;            .header-height;            .goods {                .scroll-view(100%);                padding-bottom: .5rem;            }        }    }</style>