<template>    <div class="main">        <mt-header title="商品详情" fixed>            <a slot="left" @click="goBack">                <mt-button icon="back"></mt-button>            </a>        </mt-header>        <div class="container">            <mt-swipe class="banner" :auto="2000" :show-indicators="true">                <mt-swipe-item v-for="(i, x) in goods.thumb_url" :key="x">                    <img class="silder" :src="i" >                </mt-swipe-item>            </mt-swipe>            <v-title :info="goods"></v-title>            <div class="pic">                <!--<top-bar ref="s" :list="tab"></top-bar>-->                <div class="pic-title">                    <span class="title-text">                        图文详情                    </span>                </div>                <div class="info" v-html="goods.content"></div>            </div>        </div>        <div class="fixed-btm">            <div class="btn purchase"  @click="popup('groupConfirm')">                <div class="top">                    ￥{{goods.singleprice}}                </div>                <div>单独购买</div>            </div>            <div class="btn"  @click="popup('groupAction')">                <div class="top">                    ￥{{goods.groupsprice}}                </div>                <div>2人成团</div>            </div>            <!--<div class="right gay" v-else>售罄</div>-->        </div>    </div></template><script>    import util from '../../../api/util'    import title from './title.vue'    import topBar from './../../common/Tabbar/TopBar.vue'    import {Toast} from 'mint-ui'    export default {        data(){            return{                goods:{},                groupsset:{},                shopsshare:{},                tab:[                    {                        title:'商品详情'                    },                    {                        title:'参数'                    }                ]            }        },        methods:{            goBack(){                this.$router.go(-1)            },            init(){                let _this=this;                util.request({                    url:'groups.goods.detail',                    method:'get',                    data:{                        id:_this.$route.query.id                    },                    success(res){                        console.log('拼团详情')                        console.log(res.data)                        _this.goods=res.data.goods                        _this.groupsset=res.data.groupsset                        _this.shopsshare=res.data.shopsshare                    },                    error(err){                    }                })            },            popup(name){                this.$router.push({name:name,query:{id:this.goods.id,typs:'single'}})            }        },        components:{            vTitle:title,            topBar        },        mounted(){            this.init()        }    }</script><style lang="less" scoped>    @import '../../../assets/less/index.less';    .main {        .page-view(100);        .container {            .scroll-view(100%);            .header-height;            .banner {                height: 3rem;            }            .pic {                width: 100%;                .pic-title {                    padding:.1rem;                    text-align: left;                    .title-text {                        font-weight:600;                        border-bottom:1px solid #FD5454;                        padding:.05rem 0;                        font-size: .12rem;                    }                }            }        }        .fixed-btm {            background: #fff;            position: fixed;            left: 0;            bottom: 0;            width: 100%;            height: .5rem;            display: -webkit-box;            display: -ms-flexbox;            display: flex;            -webkit-box-align: center;            -ms-flex-align: center;            align-items: center;            -webkit-box-shadow: 0 0 4px rgba(0, 0, 0, 0.1);            box-shadow: 0 0 4px rgba(0, 0, 0, 0.1);            .btn {                background: #ef4f4f;                color: #fff;                height: 100%;                /*line-height: .5rem;*/                font-size: .14rem;                flex: 1;                display: flex;                flex-direction: column;                justify-content: center;            }            .purchase {                background-color: #FF8080;            }        }    }</style>